import React from "react";
import { TweenMax } from "gsap/all";
import { Transition } from "react-transition-group";

const Ball = props => {
	const url = `/imagines/${props.index + 1}_ball.png`;
	const { in: show, remove, card } = props;
	return <Transition
		timeout={1000}
		mountOnEnter
		unmountOnExit
		appear
		in={show}
		addEndListener={(node, done) => {
			TweenMax.to(node, 0.35, {
				y: 0,
				autoAlpha: show ? 1 : 0,
				onComplete: done,
				delay: !show ? 0 : card.init ? props.index * 0.5 : 0
			});
		}}
	>
		{/* <div className="col-12 col-sm-6 col-md-4 col-lg-3 mb-3 card-container">
			<div className="card transition-card2"> */}
						<img type="img" className="jump1"
							alt=""
							// ref={img => this.logoContainer = img}
							// onMouseEnter={() => {
							// 	this.logoTween.play()
							// }}
							// onMouseLeave={() => {

							// 	this.logoTween.tweenTo(0)
							// }}
							src={url}>
						</img>
{/* 
			</div>
		</div> */}
		{/* <img type="img" className="jump1"
			alt=""
			// ref={img => this.logoContainer = img}
			// onMouseEnter={() => {
			// 	this.logoTween.play()
			// }}
			// onMouseLeave={() => {

			// 	this.logoTween.tweenTo(0)
			// }}
			src={url}>
		</img> */}

	</Transition>;
};

export default Ball;
