import React, { Component } from "react";
import { TimelineLite } from "gsap/all";
import { Transition } from "react-transition-group";

class BallRouter extends Component {

    constructor(props) {
        super(props);
        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;

        this.state = {
            show: true
        };

        this.toggleComponent = this.toggleComponent.bind(this);
    }

    componentDidMount() {
        // create logo tween
        // this.logoTween = new TimelineLite({ paused: true, repeat: -1})
        //     .to(this.logoContainer, 1, { rotation: 360, transformOrigin: "center" });
    }

    toggleComponent() {
        this.logoTween = new TimelineLite({ paused: true, repeat: -1 })
            .to(this.logoContainer, 1, { rotation: 360, transformOrigin: "center" });
    }

    render() {
        // const { show } = this.state;
        return <div className="container">

            <div className="row justify-content-center">
                {/* <Transition
                    timeout={1000}
                    mountOnEnter
                    unmountOnExit
                    in={show}
                    addEndListener={(node, done) => {
                        TimelineLite.to(node, 0.5, {
                            x: show ? 0 : 100,
                            autoAlpha: show ? 1 : 0,
                            onComplete: done
                        });
                    }}
                > */}
                    <form >
                        {/* <img className='jump' onClick={() => {
                            console.log('1');
                        }} src='/imagines/one_ball.png'></img> */}
                        <img 
                        className='jump' 
                        ref={img => this.logoContainer = img}
                        onMouseEnter={() => {
                            this.logoTween.play()
                        }} 
                        onMouseLeave={() => {
                            this.logoTween.pause()
                        }}

                        src='/imagines/two_ball.png'></img>
                    </form>

                {/* </Transition> */}
            </div>

        </div>;
    }

}

export default BallRouter;
