import React, { Component } from "react";
import { TweenMax } from "gsap/all";
import { Transition } from "react-transition-group";

class BallRouter extends Component {

    constructor(props) {
        super(props);
        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;

        this.state = {
            show: true
        };

        this.toggleComponent = this.toggleComponent.bind(this);
    }

    toggleComponent() {
        this.logoTween = new TimelineLite({ paused: true })
            .to(this.logoContainer, 1, { rotation: 360, transformOrigin: "center" });
    }


    render() {
        const { show } = this.state;
        return <div className="container">
            <div className="row">
                <div className="col-12">
                    <div className="btn-group">
                        <button
                            className="btn gsap-btn"
                            onClick={this.toggleComponent}
                        >Toggle Component</button>
                    </div>
                    <hr />
                </div>
            </div>

            <div className="row justify-content-center">
                <Transition
                    timeout={1000}
                    mountOnEnter
                    unmountOnExit
                    in={show}
                    addEndListener={(node, done) => {
                        TweenMax.to(node, 0.5, {
                            x: show ? 0 : 100,
                            autoAlpha: show ? 1 : 0,
                            onComplete: done
                        });
                    }}
                >
                    <form >
                        <img className='jump' onClick={() => {
                            console.log('1');
                        }} src='/imagines/one_ball.png'></img>
                        <img className='jump' onMouseEnter={() => {
                            console.log('enter') ;
                        }} onMouseEnter={() => {
                            console.log('leave');
                        }}
                        src='/imagines/two_ball.png'></img>
                    </form>

                </Transition>
            </div>

        </div>;
    }

}

export default BallRouter;
