import React from "react";
import { TweenMax } from "gsap/all";
import { Transition } from "react-transition-group";

const MyComboList = props => {
    const { in: show, remove, card } = props;
    console.log('Props', props);

    return <Transition
        timeout={1000}
        mountOnEnter
        unmountOnExit
        appear
        in={show}
        addEndListener={(node, done) => {
            TweenMax.to(node, 2.35, {
                y: 0,
                autoAlpha: show ? 1 : 0,
                onComplete: done,
                delay: !show ? 0 : card.init ? props.index * 0.15 : 0
            });
        }}
    >
        <div class="card myComboList justify-content-center">
            <img class="card-img-top" src="/imagines/BasicLock.gif" alt="Card image cap" />
            <button
                type="button" className="close"
                onClick={remove.bind(null, card.id)}
            >
                <span aria-hidden="true">&times;</span>
            </button>
            <div class="card-body">

                <p class="card-text text-center">{card.combo}</p>
            </div>
        </div>

    </Transition>;
};

export default MyComboList;
