import React, { Component } from "react";
import { TimelineMax } from "gsap/all";

class BallRouter extends Component {

    constructor({props}) {
        super(props);

        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo:true})
        this.logoTween.to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
    }

    render() {
        const { in: show, remove, card } = props;
        return (
            <Transition
                timeout={1000}
                mountOnEnter
                unmountOnExit
                in={show}
                addEndListener={(node, done) => {
                    TweenMax.to(node, 0.5, {
                        x: show ? 0 : 100,
                        autoAlpha: show ? 1 : 0,
                        onComplete: done
                    });
                }}
            >

            <div className="col-12 col-sm-6 col-md-4 col-lg-3 mb-3 card-container">
                <div className="card transition-card2">
                    <div className="card-body">
                        <h4 className="card-title text-center">
                            {this.props.index + 1}
                            <button type="button" className="close"
                                ref={img => this.logoContainer = img}
                                onMouseEnter={() => {
                                    this.logoTween.play()
                                }}
                                onMouseLeave={() => {

                                    this.logoTween.tweenTo(0)
                                }}

                            > 	<span aria-hidden="true">&times;</span>
                            </button>
                        </h4>
                    </div>
                </div>
            </div>
            </Transition>
        )
    }

}

export default BallRouter;
