import React, { Component } from "react";
import { TimelineMax } from "gsap/all";

class BallRouter extends Component {
        // logo container
logoContainer = null;
// logo tween
logoTween = null;
    constructor({props}) {
        super(props);
        console.log(this.props);


    }

    componentDidMount() {
       // console.log(this.props.index );

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo:true})
        this.logoTween.to(this.logoContainer, .5, { y: 10, transformOrigin: "left top" });
    }

    render() {
        const url = `/imagines/${this.props.index}_ball`;
        return <div className="container">

            <div className="row justify-content-center">
                <form >
                    <img className='jump'
                        alt=""
                        ref={img => this.logoContainer = img}
                        onMouseEnter={() => {
                            this.logoTween.play()
                        }}
                        onMouseLeave={() => {

                            this.logoTween.tweenTo(1)
                        }}
                        src={url}>
                    </img>
                </form>

            </div>

        </div>;
    }

}

export default BallRouter;
