import React, { Component } from "react";
import { TimelineMax, TweenMax} from "gsap/all";
import { Transition } from "react-transition-group";
import BallList from "../BallList/BallList.js";

class SimpleTransition extends Component {

    constructor(props) {
        super(props);
        this.state = {
            show: false
        };

    }

    componentDidMount(){
        this.setState({ show: !this.state.show });
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
        to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });

    }

    render() {
        const { show } = this.state;
        return (
            <>
                <div className="row justify-content-center">
                    <BallList cards={this.props.cards} />

                    <Transition
                        timeout={1000}
                        mountOnEnter
                        unmountOnExit

                        in={show}
                        addEndListener={(node, done) => {
                            TweenMax.to(node, 2.5, {
                                y: show ? 0 : 100,
                                autoAlpha: show ? 1 : 0,
                                onComplete: done
                            });

                        }}


                    >
                        <div className="col-10 tutorial-card simple">
                            <div className="card-body">
                                <h5 className="card-title">
                                    Animating React with GSAP
								<button
                                        type="button"
                                        className="close"
                                        onClick={() => this.setState({ show: false })}
                                    >
                                        <span aria-hidden="true">&times;</span>
                                    </button>
                                </h5>
                                <p className="card-text">This entire panel is mounted before the in animation starts and unmounted</p>
                            </div>
                        </div>
                    </Transition>

                </div>
            </>
        )
    }

}

export default SimpleTransition;
