import React, { Component } from "react";
import { Transition } from "react-transition-group";
import { TimelineMax, TweenMax } from "gsap/all";
import { TransitionGroup } from "react-transition-group";


class BallList extends Component {

    constructor({ props }) {
        super(props);

        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
        .to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
    }

    render() {
        const url = `/imagines/${props + 1}_ball.png`;
        return (
            <img type="img" className="jump1"
                alt=""
                ref={img => this.logoContainer = img}
                onMouseEnter={() => {
                    this.logoTween.play()
                }}
                onMouseLeave={() => {

                    this.logoTween.tweenTo(0)
                }}
                src={url}>
            </img>

        )
    }
}



export default BallList;
