import React, { Component } from "react";
import { TimelineMax, tween } from "gsap/all";
import { Transition } from "react-transition-group";


class BallRouter extends Component {

    constructor({props}) {
        super(props);

        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo:true})
        this.logoTween.to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
    }

    render() {
        const url = `/imagines/${this.props.index + 1}_ball.png`;
        return (
            <Transition
                timeout={1000}
                mountOnEnter
                unmountOnExit
                in={show}
                addEndListener={(node, done) => {
                    TweenMax.to(node, 0.5, {
                        x: show ? 0 : 100,
                        autoAlpha: show ? 1 : 0,
                        onComplete: done
                    });
                }}>
                <img type="img" className="jump1"
                        alt=""
                        ref={img => this.logoContainer = img}
                        onMouseEnter={() => {
                            this.logoTween.play()
                        }}
                        onMouseLeave={() => {

                            this.logoTween.tweenTo(0)
                        }}
                        src={url}>
                    </img>
            </Transition>
        )
    }

}

export default BallRouter;
