import React from "react";
import { TweenMax } from "gsap/all";
import { Transition } from "react-transition-group";

const BallAnimate = props => {
    const { in: show, card } = props;
    console.log('Props ', props);

    return <Transition
        timeout={1000}
        mountOnEnter
        unmountOnExit
        appear
        in={show}
        addEndListener={(node, done) => {
            TweenMax.to(node, 0.35, {
                y: 0,
                autoAlpha: show ? 1 : 0,
                onComplete: done,
                delay: !show ? 0 : card.init ? props.index * 0.15 : 0
            });
        }}
    >
        <img type="img" className="jump1"
            alt=""
            ref={img => this.props.logoContainer = img}
            src={`/imagines/1_ball.png`}>
        </img>
    </Transition>;
};

export default BallAnimate;
