import React, { Component } from "react";
import { Transition } from "react-transition-group";
import { TimelineMax, TweenMax } from "gsap/all";
import { TransitionGroup } from "react-transition-group";
import BallAnimate from "../BallAnimate/BallAnimate";


class BallRout extends Component {

    constructor({ props }) {
        super(props);

        this.show = true;
        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
        .to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
    }

    render() {
        // const url = `/imagines/${this.props.index + 1}_ball.png`;
        const url = `/imagines/1_ball.png`;
        return (<Transition
            timeout={1000}
            mountOnEnter
            unmountOnExit
            appear
            in={this. show}
            addEndListener={(node, done) => {
                TweenMax.to(node, 0.35, {
                    y: 0,
                    autoAlpha: this.show ? 1 : 0,
                    onComplete: done,
                    delay: !this.show ? 0 : this.props.card.init ? this.props.index * 0.15 : 0
                });
            }}
        >
            <BallAnimate 
                onMouseEnter={() => {
                    this.logoTween.play()
                }}
                onMouseLeave={() => {

                    this.logoTween.tweenTo(0)
                }}
                src={url}>
            </BallAnimate>
        </Transition>

        )
    }
}



export default BallRout;
