import React, { Component } from "react";
import { TimelineLite, TimelineMax } from "gsap/all";
import { Transition } from "react-transition-group";

class BallRouter extends Component {

    constructor(props) {
        super(props);
        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo:true})
        this.logoTween.to(this.logoContainer, .5, { y: 5, transformOrigin: "left top" });
    }

    render() {
        // const { show } = this.state;
        return <div className="container">

            <div className="row justify-content-center">
                <form >
                    <img className='jump'
                        ref={img => this.logoContainer = img}
                        onMouseEnter={() => {
                            this.logoTween.play()
                        }}
                        onMouseLeave={() => {

                            this.logoTween.tweenTo(1)
                        }}
                        src='/imagines/two_ball.png'>
                    </img>
                </form>

            </div>

        </div>;
    }

}

export default BallRouter;
