import React from "react";
import { TweenMax, TimelineMax } from "gsap/all";
import { Transition } from "react-transition-group";

const TransitionBall = props => {
    const { in: show, remove, card } = props;
    logoTween = null;
    logoContainer = null;

    const logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
    logoTween.to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });


    return <Transition
        timeout={1000}
        mountOnEnter
        unmountOnExit
        appear
        in={show}
        addEndListener={(node, done) => {
            TweenMax.to(node, 0.35, {
                y: 0,
                autoAlpha: show ? 1 : 0,
                onComplete: done,
                delay: !show ? 0 : card.init ? props.index * 0.15 : 0
            });
        }}
    >
        <div className="col-12 col-sm-6 col-md-4 col-lg-3 mb-3 card-container">
            <div className="card transition-card2">
                <div className="card-body">
                    <h4 className="card-title text-center">
                        {this.props.index + 1}
                        <button type="button" className="close"
                            ref={img => this.logoContainer = img}
                            onMouseEnter={() => {
                                this.logoTween.play()
                            }}
                            onMouseLeave={() => {

                                this.logoTween.tweenTo(0)
                            }}

                        > 	<span aria-hidden="true">&times;</span>
                        </button>
                    </h4>
                </div>
            </div>
        </div>
    </Transition>;
};

export default TransitionBall;

