//         // logo container
//         this.logoContainer = null;
//         // logo tween
//         this.logoTween = null;
//     }

//     componentDidMount() {

//         // create logo tween
//         this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo:true})
//         this.logoTween.to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
//     }

const BallRouter = props => {

    const { in: show, card } = props;
        let logoContainer = null;
        // logo tween
    let logoTween = null;

    logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
    this.logoTween.to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });

        const url = `/imagines/${props + 1}_ball.png`;
        return (
            <Transition
                timeout={1000}
                mountOnEnter
                unmountOnExit
                appear
                in={show}
                addEndListener={(node, done) => {
                    TweenMax.to(node, 0.35, {
                        y: 0,
                        autoAlpha: show ? 1 : 0,
                        onComplete: done,
                        delay: !show ? 0 : card.init ? props.index * 0.15 : 0
                    });
                }}
            >
                <img type="img" className="jump1"
                        alt=""
                        ref={img => this.logoContainer = img}
                        onMouseEnter={() => {
                            this.logoTween.play()
                        }}
                        onMouseLeave={() => {

                            this.logoTween.tweenTo(0)
                        }}
                        src={url}>
                    </img>
                </Transition>
        )
    };



export default BallRouter;
