import React, { Component } from "react";
import { TimelineMax, TweenMax } from "gsap/all";
import BallAnimate from "../BallAnimate/BallAnimate";


class BallRout extends Component {

    constructor({ props }) {
        super(props);

        this.show = true;
        // logo container
        this.logoContainer = null;
        // logo tween
        this.logoTween = null;
    }

    componentDidMount() {

        // create logo tween
        this.logoTween = new TimelineMax({ paused: true, repeat: -1, yoyo: true })
        .to(this.logoContainer, .25, { y: -10, transformOrigin: "left top" });
    }

    render() {
        // const url = `/imagines/${this.props.index + 1}_ball.png`;
        const url = `/imagines/1_ball.png`;
        return (
            <div
             ref={div => this.props.logoContainer = div}
             >
            <BallAnimate 
                onMouseEnter={() => {
                    this.logoTween.play()
                }}
                onMouseLeave={() => {

                    this.logoTween.tweenTo(0)
                }}
                src={url}>
            </BallAnimate>
        </div>

        )
    }
}



export default BallRout;
